import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const AdminPanel = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen bg-gray-100 flex">
      {/* Sidebar */}
      <div className="fixed left-0 top-0 h-full bg-blue-950 text-white w-16 hover:w-64 transition-all duration-300 ease-in-out group z-10">
        <div className="flex flex-col items-center py-8">
          <div className="mb-8">
            <h2 className="text-lg font-bold opacity-0 group-hover:opacity-100 transition-opacity duration-300">Admin Panel</h2>
          </div>
          <Link
            to="/admin/salary-management"
            className="flex items-center w-full px-4 py-3 hover:bg-blue-900 transition-colors duration-200"
          >
            <svg className="w-6 h-6 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <span className="ml-3 opacity-0 group-hover:opacity-100 transition-opacity duration-300">Salary Management</span>
          </Link>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 ml-16 group-hover:ml-64 transition-all duration-300 ease-in-out">
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-950 to-blue-600 h-32 flex items-center justify-center px-6 relative">
          <h1 className="text-4xl font-bold text-white text-center">Admin Panel</h1>
          <Link
            to="/home"
            className="absolute top-4 left-4 text-white py-2 px-4 rounded-lg shadow-lg transform transition hover:scale-105 flex items-center gap-2"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </Link>
        </div>

        {/* Content Area */}
        <div className="container mx-auto px-4 py-8 mt-10">
          <p className="text-gray-600 text-center">Hover over the sidebar to access Salary Management.</p>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
