import React, { useState, useEffect } from "react";
import axios from "axios";

const EmployeeTable = () => {
  const [employees, setEmployees] = useState([]);
  const [filteredEmployees, setFilteredEmployees] = useState([]);

  // Filters
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedYear, setSelectedYear] = useState("2025");
  const [sortBy, setSortBy] = useState(null);

  useEffect(() => {
    fetchEmployees();
  }, [selectedYear]);

  const fetchEmployees = async () => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/salaries/merged?month=October&year=${selectedYear}`
      );

      const filtered = response.data.map((emp) => ({
        employeeId: emp.employeeId,
        name: emp.name,
        base: Number(emp.base) || 15000,
        hra: Number(emp.hra) || 4000,
        conveyance: Number(emp.conveyance) || 1000,
        attendanceDays: emp.attendanceDays || 0,
        totalDays: emp.totalDays || 30,
      }));

      setEmployees(filtered);
      setFilteredEmployees(filtered);
    } catch (error) {
      console.error("Error fetching employees:", error);
    }
  };

  const calculateDeductions = (base, totalEarning, preHra) => {
    const employeePF = base * 0.12;
    const totalEsiBase = base + preHra;
    const employeeESI = totalEsiBase * 0.0075;

    const companyPF = base * 0.0367;
    const companyESI = totalEsiBase * 0.0325;
    const pension = base * 0.0833;
    const companyPay = companyPF + companyESI;

    return { employeePF, employeeESI, companyPF, companyESI, pension, companyPay };
  };

  const calculateNetPayable = (employee) => {
    const { base, hra, conveyance, attendanceDays, totalDays } = employee;
    const preBasic = (base * attendanceDays) / totalDays;
    const preHra = (hra * attendanceDays) / totalDays;
    const preConv = (conveyance * attendanceDays) / totalDays;
    const totalEarning = preBasic + preHra + preConv;

    const { employeePF, employeeESI } = calculateDeductions(preBasic, totalEarning, preHra);
    const totalDeduction = employeePF + employeeESI;
    return Math.round(totalEarning - totalDeduction);
  };

  const calculateCTC = (employee) => {
    const { base, hra, conveyance, attendanceDays, totalDays } = employee;
    const preBasic = (base * attendanceDays) / totalDays;
    const preHra = (hra * attendanceDays) / totalDays;
    const preConv = (conveyance * attendanceDays) / totalDays;
    const totalEarning = preBasic + preHra + preConv;

    const { employeePF, employeeESI, pension, companyPay } =
      calculateDeductions(preBasic, totalEarning, preHra);

    const netPayable = totalEarning - (employeePF + employeeESI);
    return Math.round(netPayable + companyPay + (employeePF + employeeESI) + pension);
  };

  const updateEmployee = (index, field, value) => {
    const updated = [...employees];
    updated[index][field] = parseFloat(value) || 0;
    setEmployees(updated);
    applyFilters(updated);
  };

  const [editing, setEditing] = useState(null);

  const handleEdit = (index) => setEditing(index);

  const handleSave = async (index) => {
    const emp = employees[index];
    setEditing(null);

    const payload = {
      employeeId: emp.employeeId,
      attendanceDays: emp.attendanceDays,
      totalDays: emp.totalDays,
      base: emp.base,
      hra: emp.hra,
      conveyance: emp.conveyance,
      netPayable: calculateNetPayable(emp),
      ctc: calculateCTC(emp),
      month: "October",
      year: selectedYear,
    };

    try {
      const res = await axios.post("http://localhost:5000/api/salaries", payload);
      console.log("Salary saved:", res.data);
      fetchEmployees(); // refresh
    } catch (error) {
      console.error("Error saving salary:", error);
    }
  };

  // Filtering logic
  const applyFilters = (data = employees) => {
    let filtered = data.filter((emp) =>
      emp.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    if (sortBy === "ctc") {
      filtered = [...filtered].sort((a, b) => calculateCTC(b) - calculateCTC(a));
    } else if (sortBy === "net") {
      filtered = [...filtered].sort(
        (a, b) => calculateNetPayable(b) - calculateNetPayable(a)
      );
    }

    setFilteredEmployees(filtered);
  };

  useEffect(() => {
    applyFilters();
  }, [searchTerm, sortBy, employees]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">

        {/* --- Filters --- */}
        <div className="bg-white shadow-lg rounded-lg mb-6 p-6">
          <h2 className="text-lg font-semibold text-gray-700 mb-4 flex items-center gap-2">
            <span>üîç Filters</span>
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <input
              type="text"
              placeholder="Search employee..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="border rounded px-3 py-2 w-full"
            />

            <select
              value={selectedYear}
              onChange={(e) => setSelectedYear(e.target.value)}
              className="border rounded px-3 py-2 w-full"
            >
              <option value="2024">2024</option>
              <option value="2025">2025</option>
            </select>

            <button
              onClick={() => {
                setSearchTerm("");
                setSelectedYear("2025");
                setSortBy(null);
                fetchEmployees();
              }}
              className="bg-gray-200 hover:bg-gray-300 text-gray-700 rounded px-4 py-2"
            >
              Reset All
            </button>

            <div className="flex gap-2">
              <button
                onClick={() => setSortBy("ctc")}
                className={`px-4 py-2 rounded ${
                  sortBy === "ctc" ? "bg-blue-500 text-white" : "bg-gray-200"
                }`}
              >
                Sort by CTC
              </button>
              <button
                onClick={() => setSortBy("net")}
                className={`px-4 py-2 rounded ${
                  sortBy === "net" ? "bg-blue-500 text-white" : "bg-gray-200"
                }`}
              >
                Sort by Net Salary
              </button>
            </div>
          </div>
        </div>

        {/* --- Table --- */}
        <div className="bg-white shadow-lg rounded-lg overflow-hidden">
          <div className="px-6 py-4 bg-gradient-to-r from-green-400 to-blue-500">
            <h1 className="text-2xl font-bold text-white">
              Employee Salary Details
            </h1>
          </div>

          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  {[
                    "Sr No.",
                    "Name",
                    "Base Salary",
                    "HRA",
                    "Conveyance",
                    "Attendance (Days)",
                    "Net Payable",
                    "CTC",
                    "Actions",
                  ].map((head, i) => (
                    <th
                      key={i}
                      className="px-6 py-3 text-xs font-medium text-gray-500 uppercase tracking-wider"
                    >
                      {head}
                    </th>
                  ))}
                </tr>
              </thead>

              <tbody className="bg-white divide-y divide-gray-200">
                {filteredEmployees.map((employee, index) => (
                  <tr key={index} className="hover:bg-gray-50 transition-colors duration-200">
                    <td className="px-6 py-4 text-sm text-gray-900 font-medium">
                      {index + 1}
                    </td>
                    <td className="px-6 py-4 text-sm font-medium text-gray-900">
                      {employee.name}
                    </td>
                    <td className="px-6 py-4 text-sm text-gray-500">
                      ‚Çπ{employee.base.toLocaleString()}
                    </td>
                    <td className="px-6 py-4 text-sm text-gray-500">
                      ‚Çπ{employee.hra.toLocaleString()}
                    </td>
                    <td className="px-6 py-4 text-sm text-gray-500">
                      ‚Çπ{employee.conveyance.toLocaleString()}
                    </td>

                    {/* Attendance */}
                    <td className="px-6 py-4 text-sm text-gray-500">
                      {editing === index ? (
                        <div className="flex items-center space-x-2">
                          <input
                            type="number"
                            value={employee.attendanceDays}
                            onChange={(e) =>
                              updateEmployee(index, "attendanceDays", e.target.value)
                            }
                            className="w-16 px-2 py-1 border rounded"
                            min="0"
                            max={employee.totalDays}
                          />
                          <span>/ {employee.totalDays}</span>
                        </div>
                      ) : (
                        `${employee.attendanceDays}/${employee.totalDays}`
                      )}
                    </td>

                    <td className="px-6 py-4 text-sm font-semibold text-green-600">
                      ‚Çπ{calculateNetPayable(employee).toLocaleString()}
                    </td>
                    <td className="px-6 py-4 text-sm font-semibold text-blue-600">
                      ‚Çπ{calculateCTC(employee).toLocaleString()}
                    </td>

                    <td className="px-6 py-4 text-sm text-gray-500">
                      {editing === index ? (
                        <button
                          onClick={() => handleSave(index)}
                          className="px-3 py-1 bg-green-500 text-white rounded hover:bg-green-600"
                        >
                          Save
                        </button>
                      ) : (
                        <button
                          onClick={() => handleEdit(index)}
                          className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                        >
                          Edit
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmployeeTable;
