import React, { useState } from 'react';

const EmployeeTable = () => {
  const [employees, setEmployees] = useState([
    {
      name: 'John Doe',
      base: 50000,
      hra: 10000,
      conveyance: 2000,
      attendance: 20,
      netPayable: 0,
      ctc: 0
    },
    {
      name: 'Jane Smith',
      base: 55000,
      hra: 11000,
      conveyance: 2200,
      attendance: 18,
      netPayable: 0,
      ctc: 0
    },
  ]);

  const calculateNetPayable = (base, hra, conveyance, attendance) => {
    const gross = base + hra + conveyance;
    const attendanceBonus = (attendance / 31) * base;
    let pf = 0;
    if (base <= 15000) {
      pf = 0.12 * base;
    }
    let esi = 0;
    if (gross <= 21000) {
      esi = 0.0075 * gross;
    }
    return gross + attendanceBonus - pf - esi;
  };

  const calculateCTC = (netPayable) => {
    // Assuming CTC is netPayable + some employer contributions, e.g., 20%
    return netPayable * 1.2;
  };

  const handleInputChange = (index, field, value) => {
    const updatedEmployees = [...employees];
    updatedEmployees[index][field] = parseFloat(value) || 0;

    // Recalculate netPayable and ctc
    const { base, hra, conveyance, attendance } = updatedEmployees[index];
    updatedEmployees[index].netPayable = calculateNetPayable(base, hra, conveyance, attendance);
    updatedEmployees[index].ctc = calculateCTC(updatedEmployees[index].netPayable);

    setEmployees(updatedEmployees);
  };

  return (
    <div className="p-5 font-sans">
      <h1 className="text-center text-gray-800 text-2xl font-bold mb-5">Employee Salary Calculator</h1>
      <div className="overflow-x-auto">
        <table className="w-full border-collapse shadow-lg bg-white">
          <thead>
            <tr className="bg-gray-100">
              <th className="p-3 border border-gray-300 text-left">Name</th>
              <th className="p-3 border border-gray-300 text-left">Base</th>
              <th className="p-3 border border-gray-300 text-left">HRA</th>
              <th className="p-3 border border-gray-300 text-left">Conveyance</th>
              <th className="p-3 border border-gray-300 text-left">Attendance (Days)</th>
              <th className="p-3 border border-gray-300 text-left">Net Payable</th>
              <th className="p-3 border border-gray-300 text-left">CTC</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee, index) => (
              <tr key={index} className={index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                <td className="p-3 border border-gray-300">{employee.name}</td>
                <td className="p-3 border border-gray-300">
                  <input
                    type="number"
                    value={employee.base}
                    onChange={(e) => handleInputChange(index, 'base', e.target.value)}
                    className="w-full border-none bg-transparent focus:outline-none"
                  />
                </td>
                <td className="p-3 border border-gray-300">
                  <input
                    type="number"
                    value={employee.hra}
                    onChange={(e) => handleInputChange(index, 'hra', e.target.value)}
                    className="w-full border-none bg-transparent focus:outline-none"
                  />
                </td>
                <td className="p-3 border border-gray-300">
                  <input
                    type="number"
                    value={employee.conveyance}
                    onChange={(e) => handleInputChange(index, 'conveyance', e.target.value)}
                    className="w-full border-none bg-transparent focus:outline-none"
                  />
                </td>
                <td className="p-3 border border-gray-300">
                  <input
                    type="number"
                    min="0"
                    max="31"
                    value={employee.attendance}
                    onChange={(e) => handleInputChange(index, 'attendance', e.target.value)}
                    className="w-full border-none bg-transparent focus:outline-none"
                  />
                </td>
                <td className="p-3 border border-gray-300">{employee.netPayable.toFixed(2)}</td>
                <td className="p-3 border border-gray-300">{employee.ctc.toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default EmployeeTable;
