import React from 'react';
import { Link } from 'react-router-dom';

const AdminPanel = () => {

  return (
    <div className="min-h-screen bg-gray-100 flex">
      {/* Sidebar */}
      <div className="h-screen text-blue-950 w-16 hover:w-64 transition-all duration-300 ease-in-out group border-r-2 border-gray-200 ">
        <div className="flex flex-col items-center py-8 mt-20">
          <div className="mb-8 ">
          </div>
          <Link
            to="/admin/salary-management"
            className="flex items-center w-full px-4 py-3 hover:bg-gray-200 transition-colors duration-200"
          >
            <DollarSign className="w-6 h-6 text-gray-700" />
            <span className="ml-3 opacity-0 group-hover:opacity-100 transition-opacity duration-300">Salary Management</span>
          </Link>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 transition-all duration-300 ease-in-out">
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-950 to-blue-600 h-32 flex items-center justify-center px-6 relative">
          <h1 className="text-4xl font-bold text-white text-center">Admin Panel</h1>
          <Link
            to="/home"
            className="absolute top-4 left-4 text-white py-2 px-4 rounded-lg shadow-lg transform transition hover:scale-105 flex items-center gap-2"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </Link>
        </div>

        {/* Content Area */}
        <div className="container mx-auto px-4 py-8 mt-10">
          
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
