import React, { useState, useEffect } from "react";
import axios from "axios";

const EmployeeTable = () => {
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/employees");
      console.log("Fetched employees:", response.data);

      const filtered = response.data.map((emp) => ({
        _id: emp._id,
        name: `${emp.firstName || ""} ${emp.lastName || ""}`.trim(),
        base: Number(emp.base) || 15000,
        hra: Number(emp.hra) || 4000,
        conveyance: Number(emp.conveyance) || 1000,
        attendanceDays: 0,
        totalDays: 30,
      }));

      setEmployees(filtered);
    } catch (error) {
      console.error("Error fetching employees:", error);
    }
  };

  const calculateDeductions = (base, totalEarning, preHra) => {
    const employeePF = base * 0.12;
    const totalEsiBase = base + preHra;
    const employeeESI = totalEsiBase * 0.0075;

    const companyPF = base * 0.0367;
    const companyESI = totalEsiBase * 0.0325;
    const pension = base * 0.0833;
    const companyPay = companyPF + companyESI;
    console.log(
      "Deductions =>",
      "Employee PF:",
      employeePF.toFixed(2),
      "| Employee ESI:",
      employeeESI.toFixed(2),
      "| Company PF:",
      companyPF.toFixed(2),
      "| Company ESI:",
      companyESI.toFixed(2),
      "| Pension:",
      pension.toFixed(2),
      "| Company Pay:",
      companyPay.toFixed(2),
      "|totalESIBase:",
      totalEsiBase.toFixed(2),
      "|hra:",
      preHra.toFixed(2),
      "|base:",
      base.toFixed(2)
    );

    return {
      employeePF,
      employeeESI,
      companyPF,
      companyESI,
      pension,
      companyPay,
    };
  };

  const calculateNetPayable = (employee) => {
    const { base, hra, conveyance, attendanceDays, totalDays } = employee;

    const preBasic = (base * attendanceDays) / totalDays;
    const preHra = (hra * attendanceDays) / totalDays;
    const preConv = (conveyance * attendanceDays) / totalDays;
    const totalEarning = preBasic + preHra + preConv;

    const { employeePF, employeeESI } = calculateDeductions(
      preBasic,
      totalEarning,
      preHra
    );
    const totalDeduction = employeePF + employeeESI;

    const netPayable = totalEarning - totalDeduction;
    return Math.round(netPayable);
  };

  const calculateCTC = (employee) => {
    const { base, hra, conveyance, attendanceDays, totalDays } = employee;

    const preBasic = (base * attendanceDays) / totalDays;
    const preHra = (hra * attendanceDays) / totalDays;
    const preConv = (conveyance * attendanceDays) / totalDays;
    const totalEarning = preBasic + preHra + preConv;

    const { employeePF, employeeESI, pension, companyPay } =
      calculateDeductions(preBasic, totalEarning, preHra);

    const netPayable = totalEarning - (employeePF + employeeESI);

    const ctc = netPayable + companyPay + (employeePF + employeeESI) + pension;

    return Math.round(ctc);
  };

  const updateEmployee = (index, field, value) => {
    const updatedEmployees = [...employees];
    updatedEmployees[index][field] = parseFloat(value) || 0;
    setEmployees(updatedEmployees);
  };

  const [editing, setEditing] = useState(null);

  const handleEdit = (index) => {
    setEditing(index);
  };

  const handleSave = async (index) => {
    const emp = employees[index];
    setEditing(null);

    const payload = {
      employeeId: emp._id, // make sure backend sends _id
      attendanceDays: emp.attendanceDays,
      totalDays: emp.totalDays,
      base: emp.base,
      hra: emp.hra,
      conveyance: emp.conveyance,
      netPayable: calculateNetPayable(emp),
      ctc: calculateCTC(emp),
      month: "October",
      year: 2025,
    };

    try {
      const res = await axios.post(
        "http://localhost:5000/api/salaries",
        payload
      );
      console.log("Salary saved:", res.data);
    } catch (error) {
      console.error("Error saving salary:", error);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="bg-white shadow-lg rounded-lg overflow-hidden">
          <div className="px-6 py-4 bg-gradient-to-r from-green-400 to-blue-500">
            <h1 className="text-2xl font-bold text-white">
              Employee Salary Details
            </h1>
          </div>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50 ">
                <tr>
                  <th className="py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Name
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Base Salary
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    HRA
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Conveyance
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Attendance (Days)
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Net Payable
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    CTC
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200 ">
                {employees.map((employee, index) => (
                  <tr
                    key={index}
                    className="hover:bg-gray-50 transition-colors duration-200 "
                  >
                    <td className=" py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                      {employee.name}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {editing === index ? (
                        <input
                          type="number"
                          value={employee.base}
                          onChange={(e) =>
                            updateEmployee(index, "base", e.target.value)
                          }
                          className="w-full px-2 py-1 border rounded"
                        />
                      ) : (
                        `₹${employee.base.toLocaleString()}`
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {editing === index ? (
                        <input
                          type="number"
                          value={employee.hra}
                          onChange={(e) =>
                            updateEmployee(index, "hra", e.target.value)
                          }
                          className="w-full px-2 py-1 border rounded"
                        />
                      ) : (
                        `₹${employee.hra.toLocaleString()}`
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {editing === index ? (
                        <input
                          type="number"
                          value={employee.conveyance}
                          onChange={(e) =>
                            updateEmployee(index, "conveyance", e.target.value)
                          }
                          className="w-full px-2 py-1 border rounded"
                        />
                      ) : (
                        `₹${employee.conveyance.toLocaleString()}`
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {editing === index ? (
                        <div className="flex items-center space-x-2">
                          <input
                            type="number"
                            value={employee.attendanceDays}
                            onChange={(e) =>
                              updateEmployee(
                                index,
                                "attendanceDays",
                                e.target.value
                              )
                            }
                            className="w-16 px-2 py-1 border rounded"
                            min="0"
                            max={employee.totalDays}
                          />
                          <span>/ {employee.totalDays}</span>
                        </div>
                      ) : (
                        `${employee.attendanceDays}/${employee.totalDays}`
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-semibold text-green-600">
                      ₹{calculateNetPayable(employee).toLocaleString()}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-semibold text-blue-600">
                      ₹{calculateCTC(employee).toLocaleString()}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {editing === index ? (
                        <button
                          onClick={() => handleSave(index)}
                          className="px-3 py-1 bg-green-500 text-white rounded hover:bg-green-600"
                        >
                          Save
                        </button>
                      ) : (
                        <button
                          onClick={() => handleEdit(index)}
                          className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                        >
                          Edit
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmployeeTable;
