import { CircleDollarSign } from 'lucide-react';
import React from 'react';
import { Link } from 'react-router-dom';

const Sidebar = () => {
  return (
    <div className="h-screen bg-gray-100 text-blue-950 w-20 hover:w-64 transition-all duration-300 ease-in-out group  border-r-2 border-gray-200 shadow-lg flex flex-col items-center">
        <div className="mt-8 flex justify-center">
            <img src='/logo2.png' alt="Logo" className="w-13 h-12 px-2" />
        </div>
      <div className="flex flex-col items-center mt-28">
        <Link
          to="/admin/salary-management"
          className="flex items-center w-full px-6 py-3 hover:bg-gray-200 transition-colors duration-200"
        >
          <CircleDollarSign />
          <span className="ml-3 invisible group-hover:visible">Salary Management</span>
        </Link>
      </div>
    </div>
  );
};

export default Sidebar;
