import { CircleDollarSign, BarChart3, Users } from 'lucide-react';
import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = () => {
  const location = useLocation();

  const menuItems = [
    {
      to: '/admin',
      icon: BarChart3,
      label: 'Dashboard',
    },
    {
      to: '/employee-table',
      icon: Users,
      label: 'Employee Table',
    },
    {
      to: '/admin/salary-management',
      icon: CircleDollarSign,
      label: 'Salary Management',
    },
  ];

  return (
    <div className="h-screen bg-gradient-to-b from-blue-950 to-blue-900 text-white w-20 hover:w-64 transition-all duration-300 ease-in-out group border-r-2 border-blue-800 shadow-xl flex flex-col items-center">

      {/* Logo */}
      <div className="mt-8 flex justify-center">
        <img src="/logo2.png" alt="Logo" className="w-12 h-10 rounded-full border-2 border-white" />
      </div>

      {/* Menu */}
      <div className="flex flex-col items-center mt-16 space-y-2 w-full">
        {menuItems.map((item) => {
          const Icon = item.icon;
          const isActive = location.pathname === item.to;
          return (
            <Link
              key={item.to}
              to={item.to}
              className={`flex items-center w-full px-3 py-3 rounded-lg transition-all duration-200 ${
                isActive
                  ? 'bg-blue-700 text-white shadow-lg'
                  : 'hover:bg-blue-800 hover:text-gray-100'
              }`}
            >
              {/* Icon always visible */}
              <div className="flex justify-center w-10">
                <Icon className="w-6 h-6 flex-shrink-0" />
              </div>

              {/* Text visible only on expand */}
              <span className="opacity-0 group-hover:opacity-100 transition-opacity duration-200 whitespace-nowrap">
                {item.label}
              </span>
            </Link>
          );
        })}
      </div>

    </div>
  );
};

export default Sidebar;
