import React, { useState } from "react";

const CalculateEmi = () => {
  const [base, setBase] = useState(15000);
  const [hra, setHra] = useState(4000);
  const [conveyance, setConveyance] = useState(1000);
  const [attendanceDays, setAttendanceDays] = useState(30);
  const [totalDays] = useState(30);

  const calculateDeductions = (preBasic, totalEarning, preHra) => {
    const employeePF = preBasic * 0.12;
    const totalEsiBase = preBasic + preHra;
    const employeeESI = totalEsiBase * 0.0075;
    const employerPF = preBasic * 0.12;
    const employerESI = totalEsiBase * 0.0325;
    const pension = employerPF * 0.0833;
    const companyPay = employerPF + employerESI;

    return {
      employeePF,
      employeeESI,
      pension,
      companyPay,
    };
  };

  const calculateCTC = (employee) => {
    const { base, hra, conveyance, attendanceDays, totalDays } = employee;

    const preBasic = (base * attendanceDays) / totalDays;
    const preHra = (hra * attendanceDays) / totalDays;
    const preConv = (conveyance * attendanceDays) / totalDays;
    const totalEarning = preBasic + preHra + preConv;

    const { employeePF, employeeESI, pension, companyPay } =
      calculateDeductions(preBasic, totalEarning, preHra);

    const netPayable = totalEarning - (employeePF + employeeESI);

    const ctc = netPayable + companyPay + (employeePF + employeeESI) + pension;

    return Math.round(ctc);
  };

  const calculateEMI = () => {
    const employee = { base, hra, conveyance, attendanceDays, totalDays };
    return calculateCTC(employee);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md mx-auto bg-white shadow-lg rounded-lg overflow-hidden">
        <div className="px-6 py-4 bg-blue-950">
          <h1 className="text-2xl font-bold text-white">Calculate EMI</h1>
        </div>
        <div className="px-6 py-4">
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Base Salary
              </label>
              <input
                type="number"
                value={base}
                onChange={(e) => setBase(parseFloat(e.target.value) || 0)}
                className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                HRA
              </label>
              <input
                type="number"
                value={hra}
                onChange={(e) => setHra(parseFloat(e.target.value) || 0)}
                className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Conveyance
              </label>
              <input
                type="number"
                value={conveyance}
                onChange={(e) => setConveyance(parseFloat(e.target.value) || 0)}
                className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              />
            </div>
            <div className="text-sm text-gray-600">
              Attendance Days: {attendanceDays} / {totalDays}
            </div>
            <div className="bg-gray-50 p-4 rounded-md">
              <h2 className="text-lg font-semibold text-gray-900">
                Calculated EMI: â‚¹{calculateEMI().toLocaleString()}
              </h2>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CalculateEmi;
