import React, { useState } from 'react';

const EmployeeTable = () => {
  const [employees, setEmployees] = useState([
    {
      name: 'John Doe',
      base: 50000,
      hra: 10000,
      conveyance: 2000,
      attendance: 20,
      netPayable: 0,
      ctc: 0
    },
    {
      name: 'Jane Smith',
      base: 55000,
      hra: 11000,
      conveyance: 2200,
      attendance: 18,
      netPayable: 0,
      ctc: 0
    },
  ]);

  const calculateNetPayable = (base, hra, conveyance, attendance) => {
    const gross = base + hra + conveyance;
    const attendanceBonus = (attendance / 31) * base;
    let pf = 0;
    if (base <= 15000) {
      pf = 0.12 * base;
    }
    let esi = 0;
    if (gross <= 21000) {
      esi = 0.0075 * gross;
    }
    return gross + attendanceBonus - pf - esi;
  };

  const calculateCTC = (netPayable) => {
    // Assuming CTC is netPayable + some employer contributions, e.g., 20%
    return netPayable * 1.2;
  };

  const handleInputChange = (index, field, value) => {
    const updatedEmployees = [...employees];
    updatedEmployees[index][field] = parseFloat(value) || 0;

    // Recalculate netPayable and ctc
    const { base, hra, conveyance, attendance } = updatedEmployees[index];
    updatedEmployees[index].netPayable = calculateNetPayable(base, hra, conveyance, attendance);
    updatedEmployees[index].ctc = calculateCTC(updatedEmployees[index].netPayable);

    setEmployees(updatedEmployees);
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>Employee Salary Calculator</h1>
      <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px', boxShadow: '0 0 10px rgba(0,0,0,0.1)' }}>
        <thead>
          <tr style={{ backgroundColor: '#f4f4f4' }}>
            <th style={{ padding: '10px', border: '1px solid #ddd' }}>Name</th>
            <th style={{ padding: '10px', border: '1px solid #ddd' }}>Base</th>
            <th style={{ padding: '10px', border: '1px solid #ddd' }}>HRA</th>
            <th style={{ padding: '10px', border: '1px solid #ddd' }}>Conveyance</th>
            <th style={{ padding: '10px', border: '1px solid #ddd' }}>Attendance (Days)</th>
            <th style={{ padding: '10px', border: '1px solid #ddd' }}>Net Payable</th>
            <th style={{ padding: '10px', border: '1px solid #ddd' }}>CTC</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((employee, index) => (
            <tr key={index} style={{ backgroundColor: index % 2 === 0 ? '#fff' : '#f9f9f9' }}>
              <td style={{ padding: '10px', border: '1px solid #ddd' }}>{employee.name}</td>
              <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                <input
                  type="number"
                  value={employee.base}
                  onChange={(e) => handleInputChange(index, 'base', e.target.value)}
                  style={{ width: '100%', border: 'none', background: 'transparent' }}
                />
              </td>
              <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                <input
                  type="number"
                  value={employee.hra}
                  onChange={(e) => handleInputChange(index, 'hra', e.target.value)}
                  style={{ width: '100%', border: 'none', background: 'transparent' }}
                />
              </td>
              <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                <input
                  type="number"
                  value={employee.conveyance}
                  onChange={(e) => handleInputChange(index, 'conveyance', e.target.value)}
                  style={{ width: '100%', border: 'none', background: 'transparent' }}
                />
              </td>
              <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                <input
                  type="number"
                  min="0"
                  max="31"
                  value={employee.attendance}
                  onChange={(e) => handleInputChange(index, 'attendance', e.target.value)}
                  style={{ width: '100%', border: 'none', background: 'transparent' }}
                />
              </td>
              <td style={{ padding: '10px', border: '1px solid #ddd' }}>{employee.netPayable.toFixed(2)}</td>
              <td style={{ padding: '10px', border: '1px solid #ddd' }}>{employee.ctc.toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EmployeeTable;
