import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Sidebar from './Sidebar';
import { fetchSalaryConfig, updateSalaryConfig } from '../../services/salaryConfigApi';

const SalaryManagement = () => {
  const [pfEmployee, setPfEmployee] = useState(12);
  const [pfEmployer, setPfEmployer] = useState(3.67);
  const [pfPension, setPfPension] = useState(8.33);
  const [pfMin, setPfMin] = useState(15000);
  const [pfMax, setPfMax] = useState(15000);
  const [esiEmployee, setEsiEmployee] = useState(0.75);
  const [esiEmployer, setEsiEmployer] = useState(3.25);
  const [esiMin, setEsiMin] = useState(21000);
  const [esiMax, setEsiMax] = useState(21000);
  const [otherCharges, setOtherCharges] = useState(0);
  const [empContributionInput, setEmpContributionInput] = useState(12.75);
  const [employerContributionInput, setEmployerContributionInput] = useState(6.92);

  const empContribution = pfEmployee + esiEmployee;
  const employerContribution = pfEmployer + esiEmployer;

  useEffect(() => {
    const loadConfig = async () => {
      try {
        const config = await fetchSalaryConfig();
        if (config) {
          setPfEmployee(config.employeePF || 12);
          setPfEmployer(config.companyPF || 3.67);
          setPfPension(config.companyPension || 8.33);
          setPfMin(config.pfThresholdMin || 0);
          setPfMax(config.pfThresholdMax || 0);
          setEsiEmployee(config.employeeESI || 0.75);
          setEsiEmployer(config.companyESI || 3.25);
          setEsiMin(config.esiThresholdMin || 21000);
          setEsiMax(config.esiThresholdMax || 21000);
        }
      } catch (error) {
        console.error("Failed to load salary config:", error);
      }
    };
    loadConfig();
  }, []);

  const handleSave = async () => {
    try {
      const payload = {
        employeePF: pfEmployee,
        employeeESI: esiEmployee,
        companyPF: pfEmployer,
        companyESI: esiEmployer,
        companyPension: pfPension,
        pfThresholdMin: pfMin,
        pfThresholdMax: pfMax,
        esiThresholdMin: esiMin,
        esiThresholdMax: esiMax,
      };
      await updateSalaryConfig(payload);
      alert("Salary settings saved successfully!");
    } catch (error) {
      console.error("Failed to save salary config:", error);
      alert("Failed to save salary settings.");
    }
  };
  return (
    <div className="min-h-screen bg-gray-100 flex">
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 transition-all duration-300 ease-in-out">
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-950 to-blue-600 h-32 flex items-center justify-center px-6 relative">
          <h1 className="text-4xl font-bold text-white text-center">Salary Management Settings</h1>
          <Link
            to="/admin"
            className="absolute top-4 left-4 text-white py-2 px-4 rounded-lg shadow-lg transform transition hover:scale-105 flex items-center gap-2"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </Link>
        </div>

        {/* Main Content */}
        <div className="container mx-auto px-4 py-8 mt-10">
          {/* Salary Management Section */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">Configure Salary Calculation Settings</h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* PF Settings */}
            <div className="bg-gray-50 rounded-lg p-4">
              <h3 className="text-lg font-semibold text-gray-800 mb-3">Provident Fund (PF)</h3>
              <div className="space-y-3">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Employee Contribution (%)</label>
                  <input
                    type="number"
                    step="0.01"
                    value={pfEmployee}
                    onChange={(e) => setPfEmployee(parseFloat(e.target.value) || 0)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="12.00"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Employer Contribution (%)</label>
                  <input
                    type="number"
                    step="0.01"
                    value={pfEmployer}
                    onChange={(e) => setPfEmployer(parseFloat(e.target.value) || 0)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="3.67"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Pension Fund (%)</label>
                  <input
                    type="number"
                    step="0.01"
                    value={pfPension}
                    onChange={(e) => setPfPension(parseFloat(e.target.value) || 0)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="8.33"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">PF Threshold Min (₹)</label>
                  <input
                    type="number"
                    value={pfMin}
                    onChange={(e) => setPfMin(parseFloat(e.target.value) || 0)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="15000"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">PF Threshold Max (₹)</label>
                  <input
                    type="number"
                    value={pfMax}
                    onChange={(e) => setPfMax(parseFloat(e.target.value) || 0)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="15000"
                  />
                </div>
              </div>
            </div>

            {/* ESI Settings */}
            <div className="bg-gray-50 rounded-lg p-4">
              <h3 className="text-lg font-semibold text-gray-800 mb-3">Employee State Insurance (ESI)</h3>
              <div className="space-y-3">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Employee Contribution (%)</label>
                  <input
                    type="number"
                    step="0.01"
                    value={esiEmployee}
                    onChange={(e) => setEsiEmployee(parseFloat(e.target.value) || 0)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="0.75"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Employer Contribution (%)</label>
                  <input
                    type="number"
                    step="0.01"
                    value={esiEmployer}
                    onChange={(e) => setEsiEmployer(parseFloat(e.target.value) || 0)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="3.25"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">ESI Threshold Min (₹)</label>
                  <input
                    type="number"
                    value={esiMin}
                    onChange={(e) => setEsiMin(parseFloat(e.target.value) || 0)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="21000"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">ESI Threshold Max (₹)</label>
                  <input
                    type="number"
                    value={esiMax}
                    onChange={(e) => setEsiMax(parseFloat(e.target.value) || 0)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="21000"
                  />
                </div>
              </div>
            </div>


          </div>

          {/* Additional Information Block */}
          <div className="bg-gray-50 rounded-lg p-4 mt-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-3">Additional Information</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="bg-white rounded-lg p-3 shadow-sm">
                <label className="block text-sm font-medium text-gray-700 mb-1">Emp Contribution (%)</label>
                <input
                  type="number"
                  step="0.01"
                  value={empContributionInput}
                  onChange={(e) => setEmpContributionInput(parseFloat(e.target.value) || 0)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="12.75"
                />
              </div>
              <div className="bg-white rounded-lg p-3 shadow-sm">
                <label className="block text-sm font-medium text-gray-700 mb-1">Employer Contribution (%)</label>
                <input
                  type="number"
                  step="0.01"
                  value={employerContributionInput}
                  onChange={(e) => setEmployerContributionInput(parseFloat(e.target.value) || 0)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="6.92"
                />
              </div>
              <div className="bg-white rounded-lg p-3 shadow-sm">
                <label className="block text-sm font-medium text-gray-700 mb-1">Other Charges (Company) (%)</label>
                <input
                  type="number"
                  step="0.01"
                  value={otherCharges}
                  onChange={(e) => setOtherCharges(parseFloat(e.target.value) || 0)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="0.00"
                />
              </div>
            </div>
          </div>

          {/* Save Button */}
          <div className="mt-6 text-center">
            <button
              onClick={handleSave}
              className="bg-blue-950 hover:bg-blue-900 text-white font-bold py-2 px-6 rounded-lg"
            >
              Save Salary Settings
            </button>
          </div>
        </div>
      </div>
    </div>
    </div>
  );
};

export default SalaryManagement;
