import React from 'react';
import { Link } from 'react-router-dom';

const Sidebar = () => {
  return (
    <div className="h-screen bg-blue-950 text-white w-16 hover:w-64 transition-all duration-300 ease-in-out group">
      <div className="flex flex-col items-center py-8">
        <div className="mb-8">
          <h2 className="text-lg font-bold opacity-0 group-hover:opacity-100 transition-opacity duration-300">Admin Panel</h2>
        </div>
        <Link
          to="/admin/salary-management"
          className="flex items-center w-full px-4 py-3 hover:bg-blue-900 transition-colors duration-200"
        >
          <svg className="w-6 h-6 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
          <span className="ml-3 invisible group-hover:visible">Salary Management</span>
        </Link>
      </div>
    </div>
  );
};
