import React, { useState, useEffect } from "react";
import axios from "axios";

const EmployeeTable = () => {
  const [employees, setEmployees] = useState([]);
  const [filteredEmployees, setFilteredEmployees] = useState([]);
  const [editing, setEditing] = useState(null);

  const [selectedMonth, setSelectedMonth] = useState("All");
  const [selectedBranch, setSelectedBranch] = useState("All");
  const [selectedYear, setSelectedYear] = useState("2025");
  const [sortBy, setSortBy] = useState({ field: "ctc", order: "asc" });

  const months = [
    "All",
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  const years = ["2024", "2025", "2026"];
  const branches = ["All", "Indore", "Mumbai", "Pune", "Remote"];


  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const res = await axios.get(
        "http://localhost:5000/api/salaries/merged?month=October&year=2025"
      );

      const filtered = res.data.map((emp) => ({
        employeeId: emp.employeeId,
        name: emp.name,
        branch: emp.branch || "Indore",
        base: Number(emp.baseSalary) || 15000,
        hra: Number(emp.hra) || 4000,
        conveyance: Number(emp.conveyance) || 1000,
        attendanceDays: emp.attendanceDays || 0,
        totalDays: emp.totalDays || 30,
      }));

      setEmployees(filtered);
      setFilteredEmployees(filtered);
    } catch (error) {
      console.error("Error fetching employees:", error);
    }
  };

  const calculateDeductions = (base, totalEarning, preHra) => {
    const employeePF = base * 0.12;
    const totalEsiBase = base + preHra;
    const employeeESI = totalEsiBase * 0.0075;

    const companyPF = base * 0.0367;
    const companyESI = totalEsiBase * 0.0325;
    const pension = base * 0.0833;
    const companyPay = companyPF + companyESI;

    return {
      employeePF,
      employeeESI,
      companyPF,
      companyESI,
      pension,
      companyPay,
    };
  };

  const calculateNetPayable = (emp) => {
    const { base, hra, conveyance, attendanceDays, totalDays } = emp;

    const preBasic = (base * attendanceDays) / totalDays;
    const preHra = (hra * attendanceDays) / totalDays;
    const preConv = (conveyance * attendanceDays) / totalDays;
    const totalEarning = preBasic + preHra + preConv;

    const { employeePF, employeeESI } = calculateDeductions(
      preBasic,
      totalEarning,
      preHra
    );
    const totalDeduction = employeePF + employeeESI;
    const netPayable = totalEarning - totalDeduction;

    return Math.round(netPayable);
  };

  const calculateCTC = (emp) => {
    const { base, hra, conveyance, attendanceDays, totalDays } = emp;

    const preBasic = (base * attendanceDays) / totalDays;
    const preHra = (hra * attendanceDays) / totalDays;
    const preConv = (conveyance * attendanceDays) / totalDays;
    const totalEarning = preBasic + preHra + preConv;

    const { employeePF, employeeESI, pension, companyPay } =
      calculateDeductions(preBasic, totalEarning, preHra);

    const netPayable = totalEarning - (employeePF + employeeESI);
    const ctc = netPayable + companyPay + (employeePF + employeeESI) + pension;

    return Math.round(ctc);
  };

  const updateEmployee = (index, field, value) => {
    const updated = [...filteredEmployees];
    updated[index][field] = parseFloat(value) || 0;
    setFilteredEmployees(updated);
  };

  const handleEdit = (index) => setEditing(index);
  const handleSave = async (index) => {
    const emp = filteredEmployees[index];
    setEditing(null);

    const payload = {
      employeeId: emp.employeeId,
      attendanceDays: emp.attendanceDays,
      totalDays: emp.totalDays,
      base: emp.base,
      hra: emp.hra,
      conveyance: emp.conveyance,
      netPayable: calculateNetPayable(emp),
      ctc: calculateCTC(emp),
      month: selectedMonth === "All" ? "October" : selectedMonth,
      year: selectedYear,
    };

    try {
      const res = await axios.post("http://localhost:5000/api/salaries", payload);
      console.log("Salary saved:", res.data);
    } catch (error) {
      console.error("Error saving salary:", error);
    }
  };

  useEffect(() => {
    let data = [...employees];

    if (selectedMonth !== "All")
      data = data.filter((e) => e.month === selectedMonth);
    if (selectedBranch !== "All")
      data = data.filter((e) => e.branch === selectedBranch);

    if (sortBy.field === "ctc") {
      data.sort((a, b) =>
        sortBy.order === "asc"
          ? calculateCTC(a) - calculateCTC(b)
          : calculateCTC(b) - calculateCTC(a)
      );
    } else if (sortBy.field === "net") {
      data.sort((a, b) =>
        sortBy.order === "asc"
          ? calculateNetPayable(a) - calculateNetPayable(b)
          : calculateNetPayable(b) - calculateNetPayable(a)
      );
    }

    setFilteredEmployees(data);
  }, [selectedMonth, selectedBranch, selectedYear, sortBy, employees]);

  const resetFilters = () => {
    setSelectedMonth("All");
    setSelectedBranch("All");
    setSelectedYear("2025");
    setSortBy({ field: "ctc", order: "asc" });
  };


  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 via-blue-400 to-white py-8 px-6">
      <div className="max-w-7xl mx-auto">
        <div className="bg-gradient-to-r from-blue-500 to-blue-300 rounded-lg shadow p-5 mb-6 border border-blue-400">
          <div className="flex flex-wrap items-center gap-4 justify-between">
            <div className="flex flex-wrap items-center gap-3">
              <h2 className="font-semibold text-white flex items-center gap-2">
                <span> Filters</span>
              </h2>

              <select
                className="border border-blue-300 bg-white px-3 py-1.5 rounded-md text-sm text-gray-800"
                value={selectedMonth}
                onChange={(e) => setSelectedMonth(e.target.value)}
              >
                {months.map((m) => (
                  <option key={m}>{m}</option>
                ))}
              </select>

              <select
                className="border border-blue-300 bg-white px-3 py-1.5 rounded-md text-sm text-gray-800"
                value={selectedBranch}
                onChange={(e) => setSelectedBranch(e.target.value)}
              >
                {branches.map((b) => (
                  <option key={b}>{b}</option>
                ))}
              </select>

              <select
                className="border border-blue-300 bg-white px-3 py-1.5 rounded-md text-sm text-gray-800"
                value={selectedYear}
                onChange={(e) => setSelectedYear(e.target.value)}
              >
                {years.map((y) => (
                  <option key={y}>{y}</option>
                ))}
              </select>

              <button
                onClick={resetFilters}
                className="border border-blue-300 bg-white px-3 py-1.5 rounded-md text-sm text-gray-800 hover:bg-gray-100"
              >
                Reset All
              </button>
            </div>

            <div className="flex items-center gap-2">
              <button
                onClick={() =>
                  setSortBy((p) => ({
                    field: "ctc",
                    order: p.order === "asc" ? "desc" : "asc",
                  }))
                }
                className={`px-3 py-1.5 rounded-md text-sm font-medium border border-blue-300 ${
                  sortBy.field === "ctc"
                    ? "bg-blue-600 text-white"
                    : "bg-white text-gray-800 hover:bg-gray-100"
                }`}
              >
                ⇅ CTC {sortBy.order === "asc" ? "Low → High" : "High → Low"}
              </button>

              <button
                onClick={() =>
                  setSortBy((p) => ({
                    field: "net",
                    order: p.order === "asc" ? "desc" : "asc",
                  }))
                }
                className={`px-3 py-1.5 rounded-md text-sm font-medium border border-blue-300 ${
                  sortBy.field === "net"
                    ? "bg-blue-600 text-white"
                    : "bg-white text-gray-800 hover:bg-gray-100"
                }`}
              >
                ⇅ Net Salary
              </button>
            </div>
          </div>

          <p className="mt-2 text-sm text-white">
            Showing {filteredEmployees.length} employees
          </p>
        </div>

        <div className="bg-white shadow rounded-lg overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                {[
                  "Sr No.",
                  "Name",
                  "Base",
                  "HRA",
                  "Conveyance",
                  "Attendance",
                  "Net Payable",
                  "CTC",
                  "Actions",
                ].map((head) => (
                  <th
                    key={head}
                    className="px-6 py-3 text-xs font-semibold text-gray-600 uppercase"
                  >
                    {head}
                  </th>
                ))}
              </tr>
            </thead>

            <tbody className="divide-y divide-gray-200">
              {filteredEmployees.map((emp, index) => (
                <tr key={index} className="hover:bg-gray-50">
                  <td className="px-6 py-3">{index + 1}</td>
                  <td className="px-6 py-3">{emp.name}</td>
                  <td className="px-6 py-3">₹{emp.base}</td>
                  <td className="px-6 py-3">₹{emp.hra}</td>
                  <td className="px-6 py-3">₹{emp.conveyance}</td>
                  <td className="px-6 py-3">
                    {editing === index ? (
                      <input
                        type="number"
                        className="border px-2 py-1 w-20 rounded"
                        value={emp.attendanceDays}
                        onChange={(e) =>
                          updateEmployee(index, "attendanceDays", e.target.value)
                        }
                      />
                    ) : (
                      `${emp.attendanceDays}/${emp.totalDays}`
                    )}
                  </td>
                  <td className="px-6 py-3 text-green-600 font-semibold">
                    ₹{calculateNetPayable(emp)}
                  </td>
                  <td className="px-6 py-3 text-blue-600 font-semibold">
                    ₹{calculateCTC(emp)}
                  </td>
                  <td className="px-6 py-3">
                    {editing === index ? (
                      <button
                        onClick={() => handleSave(index)}
                        className="bg-green-500 text-white px-3 py-1 rounded"
                      >
                        Save
                      </button>
                    ) : (
                      <button
                        onClick={() => handleEdit(index)}
                        className="bg-blue-500 text-white px-3 py-1 rounded"
                      >
                        Edit
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default EmployeeTable;
