import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Sidebar from './Sidebar';
import { ChevronLeft, Users, TrendingUp, DollarSign } from 'lucide-react';
import { fetchEmployees } from '../../services/employeeTableApi';
import { fetchSalaryConfig } from '../../services/salaryConfigApi';
import { calculateCTC } from '../../utils/calculations';

const AdminPanel = () => {
  return (
    <div className="min-h-screen bg-gray-100 flex">
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 transition-all duration-300 ease-in-out ">
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-950 to-blue-600 h-32 flex items-center justify-center px-6 relative ">
          <h1 className="text-4xl font-bold text-white text-center">Admin Panel</h1>
          <Link
            to="/home"
            className="absolute top-4 left-4 text-white py-2 px-4 rounded-lg shadow-lg transform transition hover:scale-105 flex items-center gap-2"
          >
             <ChevronLeft className="w-5 h-5 text-gray-200" />
          </Link>
        </div>

        {/* Content Area */}
        <div className="container mx-auto px-4 py-8 mt-10">
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
