import React, { useState, useEffect } from "react";
import axios from "axios";
import { handleExport } from "./utils/exportUtils";

const EmployeeTable = () => {
  const [employees, setEmployees] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedMonth, setSelectedMonth] = useState("October");
  const [selectedYear, setSelectedYear] = useState("2025");
  const [selectedBranch, setSelectedBranch] = useState("All");
  const [sortBy, setSortBy] = useState(null);
  const [sortOrder, setSortOrder] = useState("asc");

  useEffect(() => {
    fetchEmployees();
  }, [selectedMonth, selectedYear, selectedBranch]);

  const fetchEmployees = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/employees`);

      console.log("Fetched employees:", response.data);

      const filtered = response.data.map((emp) => {
        // Find salary for selected month and year
        const salary = emp.salaries?.find(
          (sal) => sal.month === selectedMonth && sal.year === parseInt(selectedYear)
        );

        return {
          employeeId: emp.employeeId,
          name: `${emp.firstName || ""} ${emp.lastName || ""}`.trim(),
          base: Number(emp.baseSalary) || 15000,
          hra: Number(emp.hra) || 4000,
          conveyance: Number(emp.conveyance) || 1000,
          attendanceDays: salary ? salary.attendanceDays : 0,
          totalDays: salary ? salary.totalDays : 30,
          netPayable: salary ? salary.netPayable : 0,
          ctc: salary ? salary.ctc : 0,
          branch: emp.branch || "Main Branch",
        };
      });

      setEmployees(filtered);
    } catch (error) {
      console.error("Error fetching employees:", error);
    }
  };

  const calculateDeductions = (base, totalEarning, preHra) => {
    const employeePF = base * 0.12;
    const totalEsiBase = base + preHra;
    const employeeESI = totalEsiBase * 0.0075;

    const companyPF = base * 0.0367;
    const companyESI = totalEsiBase * 0.0325;
    const pension = base * 0.0833;
    const companyPay = companyPF + companyESI;

    return {
      employeePF,
      employeeESI,
      companyPF,
      companyESI,
      pension,
      companyPay,
    };
  };

  const calculateNetPayable = (employee) => {
    const { base, hra, conveyance, attendanceDays, totalDays } = employee;

    const preBasic = (base * attendanceDays) / totalDays;
    const preHra = (hra * attendanceDays) / totalDays;
    const preConv = (conveyance * attendanceDays) / totalDays;
    const totalEarning = preBasic + preHra + preConv;

    const { employeePF, employeeESI } = calculateDeductions(
      preBasic,
      totalEarning,
      preHra
    );
    const totalDeduction = employeePF + employeeESI;

    const netPayable = totalEarning - totalDeduction;
    return Math.round(netPayable);
  };

  const calculateCTC = (employee) => {
    const { base, hra, conveyance, attendanceDays, totalDays } = employee;

    const preBasic = (base * attendanceDays) / totalDays;
    const preHra = (hra * attendanceDays) / totalDays;
    const preConv = (conveyance * attendanceDays) / totalDays;
    const totalEarning = preBasic + preHra + preConv;

    const { employeePF, employeeESI, pension, companyPay } =
      calculateDeductions(preBasic, totalEarning, preHra);

    const netPayable = totalEarning - (employeePF + employeeESI);

    const ctc = netPayable + companyPay + (employeePF + employeeESI) + pension;

    return Math.round(ctc);
  };

  const updateEmployee = (index, field, value) => {
    const updatedEmployees = [...employees];
    updatedEmployees[index][field] = parseFloat(value) || 0;
    setEmployees(updatedEmployees);
  };

  const [editing, setEditing] = useState(null);

  const handleEdit = (index) => {
    setEditing(index);
  };

  const handleSave = async (index) => {
    const emp = employees[index];
    setEditing(null);

    const payload = {
      employeeId: emp.employeeId,
      attendanceDays: emp.attendanceDays,
      totalDays: emp.totalDays,
      base: emp.base,
      hra: emp.hra,
      conveyance: emp.conveyance,
      netPayable: calculateNetPayable(emp),
      ctc: calculateCTC(emp),
      month: selectedMonth,
      year: selectedYear,
    };

    try {
      const res = await axios.post(
        "http://localhost:5000/api/salaries",
        payload
      );
      console.log("Salary saved:", res.data);
    } catch (error) {
      console.error("Error saving salary:", error);
    }
  };

  const filteredAndSortedEmployees = employees
    .filter((emp) => emp.name.toLowerCase().includes(searchTerm.toLowerCase()))
    .filter((emp) => selectedBranch === "All" || emp.branch === selectedBranch)
    .sort((a, b) => {
      if (!sortBy) return 0;
      let aVal, bVal;
      if (sortBy === "name") {
        aVal = a.name.toLowerCase();
        bVal = b.name.toLowerCase();
      } else if (sortBy === "ctc") {
        aVal = a.ctc;
        bVal = b.ctc;
      } else if (sortBy === "netPayable") {
        aVal = a.netPayable;
        bVal = b.netPayable;
      }
      if (sortOrder === "asc") {
        return aVal > bVal ? 1 : -1;
      } else {
        return aVal < bVal ? 1 : -1;
      }
    });

  const handleSort = (column) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortBy(column);
      setSortOrder("asc");
    }
  };

  const months = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];
  const years = ["2023", "2024", "2025", "2026"];
  const branches = ["All", "Main Branch", "Branch 2", "Branch 3"];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="bg-white shadow-lg rounded-lg overflow-hidden">
          <div className="px-6 py-4 bg-gradient-to-r from-green-400 to-blue-500">
            <h1 className="text-2xl font-bold text-white">
              Employee Salary Details
            </h1>
          </div>
          <div className="px-6 py-4 bg-gray-50">
            <div className="flex flex-wrap gap-4 items-center justify-between">
              <div className="flex flex-wrap gap-4 items-center">
                <input
                  type="text"
                  placeholder="Search by name..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <select
                  value={selectedMonth}
                  onChange={(e) => setSelectedMonth(e.target.value)}
                  className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  {months.map((month) => (
                    <option key={month} value={month}>
                      {month}
                    </option>
                  ))}
                </select>
                <select
                  value={selectedYear}
                  onChange={(e) => setSelectedYear(e.target.value)}
                  className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  {years.map((year) => (
                    <option key={year} value={year}>
                      {year}
                    </option>
                  ))}
                </select>
                <select
                  value={selectedBranch}
                  onChange={(e) => setSelectedBranch(e.target.value)}
                  className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  {branches.map((branch) => (
                    <option key={branch} value={branch}>
                      {branch}
                    </option>
                  ))}
                </select>
              </div>
              <button
                onClick={() => handleExport(selectedMonth, selectedYear, filteredAndSortedEmployees)}
                className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
              >
                Export to CSV
              </button>
            </div>
          </div>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Sr No.
                  </th>
                  <th
                    className="px-6 py-3 text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer"
                    onClick={() => handleSort("name")}
                  >
                    Name {sortBy === "name" && (sortOrder === "asc" ? "↑" : "↓")}
                  </th>
                  <th className="px-6 py-3 text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Base Salary
                  </th>
                  <th className="px-6 py-3 text-xs font-medium text-gray-500 uppercase tracking-wider">
                    HRA
                  </th>
                  <th className="px-6 py-3 text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Conveyance
                  </th>
                  <th className="px-6 py-3 text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Attendance (Days)
                  </th>
                  <th
                    className="px-6 py-3 text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer"
                    onClick={() => handleSort("netPayable")}
                  >
                    Net Payable {sortBy === "netPayable" && (sortOrder === "asc" ? "↑" : "↓")}
                  </th>
                  <th
                    className="px-6 py-3 text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer"
                    onClick={() => handleSort("ctc")}
                  >
                    CTC {sortBy === "ctc" && (sortOrder === "asc" ? "↑" : "↓")}
                  </th>
                  <th className="px-6 py-3 text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredAndSortedEmployees.map((employee, index) => (
                  <tr
                    key={employee.employeeId}
                    className="hover:bg-gray-50 transition-colors duration-200"
                  >
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 font-medium">
                      {index + 1}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                      {employee.name}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {editing === index ? (
                        <input
                          type="number"
                          value={employee.base}
                          onChange={(e) =>
                            updateEmployee(index, "base", e.target.value)
                          }
                          className="w-full px-2 py-1 border rounded"
                        />
                      ) : (
                        `₹${employee.base.toLocaleString()}`
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {editing === index ? (
                        <input
                          type="number"
                          value={employee.hra}
                          onChange={(e) =>
                            updateEmployee(index, "hra", e.target.value)
                          }
                          className="w-full px-2 py-1 border rounded"
                        />
                      ) : (
                        `₹${employee.hra.toLocaleString()}`
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {editing === index ? (
                        <input
                          type="number"
                          value={employee.conveyance}
                          onChange={(e) =>
                            updateEmployee(index, "conveyance", e.target.value)
                          }
                          className="w-full px-2 py-1 border rounded"
                        />
                      ) : (
                        `₹${employee.conveyance.toLocaleString()}`
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {editing === index ? (
                        <div className="flex items-center space-x-2">
                          <input
                            type="number"
                            value={employee.attendanceDays}
                            onChange={(e) =>
                              updateEmployee(
                                index,
                                "attendanceDays",
                                e.target.value
                              )
                            }
                            className="w-16 px-2 py-1 border rounded"
                            min="0"
                            max={employee.totalDays}
                          />
                          <span>/ {employee.totalDays}</span>
                        </div>
                      ) : (
                        `${employee.attendanceDays}/${employee.totalDays}`
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-semibold text-green-600">
                      ₹{employee.netPayable.toLocaleString()}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-semibold text-blue-600">
                      ₹{employee.ctc.toLocaleString()}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {editing === index ? (
                        <button
                          onClick={() => handleSave(index)}
                          className="px-3 py-1 bg-green-500 text-white rounded hover:bg-green-600"
                        >
                          Save
                        </button>
                      ) : (
                        <button
                          onClick={() => handleEdit(index)}
                          className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                        >
                          Edit
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmployeeTable;
